import React, { Component } from 'react'
import { connect } from 'react-redux'
//import the reducer that contains the songList
//import selectSong action to use for listener
import { bindActionCreators } from 'redux'
import selectSong from '../actions';


class SongList extends Component {
//The song list needs to READ and CHANGE the state, connection of actions needed
  render() {
    const songs = this.props.songList.map(song => {
      return (
        <div 
          key={song.title} 
          onClick={() => this.props.selectSong(song)}>
          <div>{song.title}</div>
          {/* <div>{song.artist}</div>
          <div>{song.length} mins</div>
          <div>__________________</div> */}
        </div>
      )
    })
    return (
      <React.Fragment>
        <h4>SongList</h4>
        {songs} 
      </React.Fragment>
    )
  }
}

function mapStateToProps(state) {
  //here the state will go into this.props....
  return {
    songList: state.songList
  }
}

function mapDispatchToProps(dispatch) {
  return bindActionCreators({
    selectSong: selectSong
  }, dispatch)
}

export default connect(mapStateToProps, mapDispatchToProps)(SongList)
